mapper.update:
UPDATE member
SET userName=#{userName}, userPass=#{userPass}, userEmail=#{userEmail}
WHERE userId=#{userId};

mapper.delete:
DELETE FROM reply
WHERE userId=#{userId};

DELETE FROM board_qna
WHERE userId=#{userId};

DELETE FROM HOTPLACE
WHERE userId=#{userId};

DELETE FROM board
WHERE userId=#{userId};

DELETE FROM member
WHERE userId=#{userId};

mapper.update:
UPDATE member
SET refreshToken = #{refreshToken}
WHERE userId = #{userId}

mapper.select:
SELECT refreshToken
FROM member
WHERE user_id = #{userId}

mapper.update:
update member
SET refreshToken = #{token, jdbcType=VARCHAR}
WHERE userId = #{userId}
